		bool isAlpha(CodePointT codePoint) {
			return inRange(codePoint, 'a', 'z') || inRange(codePoint, 'A', 'Z') || codePoint == '_';
		}
		
		bool isNumeric(CodePointT codePoint) {
			return inRange(codePoint, '0', '9');
		}
		
		bool isAlphaNumeric(CodePointT codePoint) {
			return isAlpha(codePoint) || isNumeric(codePoint);
		}
		
		bool isHexadecimal(CodePointT codePoint) {
			return isNumeric(codePoint) || inRange(codePoint, 'A', 'F') || codePoint == '_';
		}
		
		bool isTab(CodePointT codePoint) {
			return codePoint == '\t';
		}
		
		bool isSpace(CodePointT codePoint) {
			return codePoint == ' ';
		}
		
		bool isWhitespace(CodePointT codePoint) {
			return codePoint == ' ' || codePoint == '\t' || codePoint == '\r' || codePoint == '\n';
		}
		
		bool isNewline(CodePointT codePoint) {
			return codePoint == '\r' || codePoint == '\n';
		}
		
		bool isNotNewline(CodePointT codePoint) {
			return !isNewline(codePoint);
		}
